    private void t1KeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_t1KeyTyped
        // TODO add your handling code here:
        char c=evt.getKeyChar();
        if(!(Character.isDigit(c)||(c==KeyEvent.VK_BACK_SPACE)|| c==KeyEvent.VK_DELETE))
        {
          getToolkit().beep();
          evt.consume();
        }
    }//GEN-LAST:event_t1KeyTyped

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        Connection con=null;
       PreparedStatement ps=null;
       ResultSet rs=null;
       int bookid=Integer.parseInt(t1.getText());
       String bookname=t2.getText();
       String authorname=t3.getText();
       String almirahno=t4.getText();
       try
       {
        Class.forName("oracle.jdbc.driver.OracleDriver");
       }
       catch(Exception ex)
       {
        ex.printStackTrace();
       }
       
       try
       {
         con=DriverManager.getConnection("jdbc:oracle:thin:@localhost:1521:XE","amit","54321");
         ps=con.prepareStatement("select book_id from add_book where book_id='"+bookid+"'");
         rs=ps.executeQuery();
         if(rs.next())
         {
           info.setText(bookid+" already exist");
         }
        
       ps=con.prepareStatement("insert into add_book values('"+bookid+"','"+bookname+"','"+authorname+"','"+branch+"','"+almirahno+"')");
       rs=ps.executeQuery();
       if(rs.next())
       {
         
         JOptionPane.showMessageDialog(null,"Book Added Successfully!");
        t1.setText("");
        t2.setText("");
        t3.setText("");
        t4.setText("");
        ct1.setSelectedIndex(0);
        info.setText("");
       }
       else
       {
          JOptionPane.showMessageDialog(null,"Please try Again for Add Book!");
       }
       }
       catch(Exception ex)
       {
       ex.printStackTrace();
       }

    }//GEN-LAST:event_jButton1ActionPerformed

