    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String name=t11.getText();
        String username=t22.getText();
        String ans=t33.getText();
        String pass=t44.getText();
        String conpass=t55.getText();
        int secqure=ct1.getSelectedIndex();
        String mono=libmo.getText();
        Connection con=null;
        PreparedStatement ps=null;
        ResultSet rs=null;
        try
        {
            Class.forName("oracle.jdbc.driver.OracleDriver");
        }
        catch(Exception ex)
        {
            ex.printStackTrace();
        }
        try
        {
            con=DriverManager.getConnection("jdbc:oracle:thin:@localhost:1521:XE","amit","54321");
            ps=con.prepareStatement("insert into adminreg values(regno.nextVal,'"+name+"','"+username+"','"+ans+"','"+pass+"','"+secqure+"','"+mono+"')");
            rs=ps.executeQuery();
            if(rs.next())
            {
                JOptionPane.showMessageDialog(null,"Registration Successfully");
            }
            else
            {
                JOptionPane.showMessageDialog(null,"Registration Failed");
            }
        }
        catch(Exception ex)
        {
            JOptionPane.showMessageDialog(null,ex);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

