    private void libmoKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_libmoKeyTyped
        // TODO add your handling code here:
        char c=evt.getKeyChar();
        if(!(Character.isDigit(c)||(c==KeyEvent.VK_BACK_SPACE)|| c==KeyEvent.VK_DELETE))
        {
          getToolkit().beep();
          evt.consume();
        }
    }//GEN-LAST:event_libmoKeyTyped

    private void t55FocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_t55FocusLost
        // TODO add your handling code here:
        if(t44.getText().matches(t55.getText()))
        {
          conpass.setText("");
        }
        else
        {
          conpass.setText("Password Not Matched!");
          conpass.setForeground(Color.red);
        }
    }//GEN-LAST:event_t55FocusLost

